import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Title from './Title';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Query from './Queries';

const useStyles = makeStyles((theme) => ({
  seeMore: {
    marginTop: theme.spacing(3),
  },
}));

// const rows = [];

function createData(id, result) {
  return { id, result };
}

function parseJSON(response) {
  return response.json();
}

class QueryResults extends React.Component {
  constructor(props) {
    super(props);
    this.handleQuery = this.handleQuery.bind(this);
    this.state = { rows: [] };
  }

  handleQuery() {
    alert('The button was clicked.');
    fetch('/campaign', {
      accept: 'application/json',
    })
      .then(parseJSON)
      .then((result) => {
        this.setState({
          rows: [createData(0, result)],
        });
      })
      .then(console.log(this.state.rows));
  }

  render() {
    const rows = this.state.rows;

    let button2;

    button2 = <SubmitButton onClick={this.handleQuery} />;

    return (
      <div>
        <DisplayRows rows={rows} />
        {button2}
      </div>
    );
  }
}

function UserGreeting(props) {
  return <h1>{props.rows}</h1>;
}

function GuestGreeting(props) {
  return <h1>No results found.</h1>;
}

function Results(props) {
  const rows = props.rows;
  return (
    <React.Fragment>
      <Title>Query Here</Title>
      <Query />
      <Table size="small">
        <TableHead>
          <TableRow>
            <TableCell>Results</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.id}>
              <TableCell>{row.result}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </React.Fragment>
  );
}

function DisplayRows(props) {
  const rows = props.rows;
  return <Results rows={rows} />;
}

function SubmitButton(props) {
  return (
    <Button variant="outlined" onClick={props.onClick}>
      Submit
    </Button>
  );
}

export default QueryResults;
