import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Title from './Title';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

const useStyles = makeStyles((theme) => ({
  seeMore: {
    marginTop: theme.spacing(3),
  },
}));

// const rows = [];

function createData(id, result) {
  return { id, result };
}

function parseJSON(response) {
  return response.json();
}

class QueryResults extends React.Component {
  constructor(props) {
    super(props);
    this.handleLoginClick = this.handleLoginClick.bind(this);
    this.handleLogoutClick = this.handleLogoutClick.bind(this);
    this.handleQuery = this.handleQuery.bind(this);
    this.state = { isLoggedIn: false, rows: [] };
  }

  handleQuery() {
    alert('The button was clicked.');
    fetch('/campaign', {
      accept: 'application/json',
    })
      .then(parseJSON)
      .then((result) => {
        this.setState({ rows: rows.push(createData(0, result)) });
      })
      .then(console.log(this.state.rows));
  }

  handleLoginClick() {
    this.setState({ isLoggedIn: true });
  }

  handleLogoutClick() {
    this.setState({ isLoggedIn: false });
  }

  render() {
    const isLoggedIn = this.state.isLoggedIn;
    const rows = this.state.rows;
    let button;
    let button2;

    if (isLoggedIn) {
      button = <LogoutButton onClick={this.handleLogoutClick} />;
    } else {
      button = <LoginButton onClick={this.handleLoginClick} />;
    }
    if (rows.length > 0) {
      button2 = <SubmitButton onClick={this.handleQuery} />;
    }

    return (
      <div>
        <Greeting isLoggedIn={isLoggedIn} />
        {button}

        <Query rows={rows} />
        {button2}
      </div>
    );
  }
}

function UserGreeting(props) {
  return <h1>You have results!</h1>;
}

function GuestGreeting(props) {
  return <h1>No results found.</h1>;
}

function Greeting(props) {
  const isLoggedIn = props.isLoggedIn;
  if (isLoggedIn) {
    return <UserGreeting />;
  }
  return <GuestGreeting />;
}

function Query(props) {
  const rows = props.rows;
  if (rows.length > 0) {
    return <UserGreeting />;
  }
  return <GuestGreeting />;
}

function LoginButton(props) {
  return <button onClick={props.onClick}>Login</button>;
}

function SubmitButton(props) {
  return <button onClick={props.onClick}>Submit</button>;
}

function LogoutButton(props) {
  return <button onClick={props.onClick}>Logout</button>;
}

export default QueryResults;
